mapper.select:

select count(*)
from video
where user_id=#{userId}


select count(*)
from comments
where user_id=#{userId}


select count(*)
from post
where writer_id=#{userId}


select count(*)
from scrap
where user_id=#{userId}

mapper.update:

update users
set user_name=#{userName},


	user_nickname=#{userNickname},


	user_tel=#{userTel},
	user_email=#{userEmail},
	user_addr=#{userAddr}


    , user_pwd=#{userPwd}


where user_id=#{userId}

mapper.delete:

update video
set video_delete=1
where video_no=#{videoNo}
