mapper.select:
select rn, noti_no notiNo, noti_title notiTitle, noti_content notiContent, to_char(noti_date, 'YYYY-MM-DD HH:MI') notiDate, noti_hit_cnt notiHitCnt, user_id userId, user_level userLevel from (select ROWNUM rn, noti_no, noti_title, noti_content, noti_date, noti_hit_cnt, user_id, user_level from (select noti_no, noti_title, noti_content, noti_date, noti_hit_cnt, us.user_id, us.user_level from notice noti, users us where noti.user_id=us.user_id
and noti_title like '%'||#{kwd}||'%'
order by noti_no desc) o) t where rn > #{startRnum} and rn <= #{endRnum}

mapper.update:

	update notice
	set noti_title=#{notiTitle}, noti_content=#{notiContent}
	where noti_no=#{notiNo}


	update notice
	set noti_hit_cnt=noti_hit_cnt+1
	where noti_no=#{notiNo}

mapper.select:
SELECT rn, qna_no, qna_title, qna_hit_cnt, to_char(qna_date, 'YYYY-MM-DD HH:MI') qna_date, user_id FROM(SELECT ROWNUM rn, qna_no, qna_title, qna_hit_cnt, qna_date, user_id FROM(SELECT qna_no, qna_title, qna_hit_cnt, qna_date, user_id FROM qna
and title like '%'||#{searchValue}|| '%'
ORDER BY qna_no desc)q_o )q_ro WHERE rn > #{startRnum, jdbcType=INTEGER} AND rn <= #{endRnum, jdbcType=INTEGER}

mapper.insert:

	INSERT INTO qna
	VALUES (seq_qna_no.nextval
			, #{qna_title, jdbcType=VARCHAR}
			, #{qna_content, jdbcType=VARCHAR}
			, sysdate
			, #{user_id, jdbcType=VARCHAR}
			, 0)

mapper.update:

	UPDATE QNA
	SET qna_hit_cnt = qna_hit_cnt + 1
	WHERE qna_no = #{qna_no}


	UPDATE qna
	SET qna_title=#{qna_title}, qna_content=#{qna_content}
	WHERE qna_no=#{qna_no}
