mapper.select:
select rn, noti_no notiNo, noti_title notiTitle, noti_content notiContent, to_char(noti_date, 'YYYY-MM-DD HH:MI') notiDate, noti_hit_cnt notiHitCnt, user_id userId from (select ROWNUM rn, noti_no, noti_title, noti_content, noti_date, noti_hit_cnt, user_id from (select noti_no, noti_title, noti_content, noti_date, noti_hit_cnt, us.user_id from notice noti, users us where noti.user_id=us.user_id
and noti_title like '%'||#{kwd}||'%'
order by noti_no desc) o) t where rn > #{startRnum} and rn <= #{endRnum}
